<?php
session_start();


// Proses login jika ada POST data
if (isset($_POST['login'])) {
    // Lakukan validasi dan proses login di sini
    // Contoh validasi sederhana
    $username = $_POST['username'];
    $password = $_POST['password'];

    // Contoh validasi login
    if ($username == 'admin' && $password == 'admin123') {
        $_SESSION['user_id'] = 1; // Contoh ID user admin
        $_SESSION['role'] = 'admin';
        header('Location: admin_dashboard.php');
        exit();
    } elseif ($username == 'yasin123' && $password == 'admin123') {
        $_SESSION['user_id'] = 2; // Contoh ID user coach
        $_SESSION['role'] = 'coach';
        header('Location: coach_dashboard.php');
        exit();
    } else {
        $error = "<h5 class='error-message'>Username atau Password salah!</h5>";
    }
    
}
?>

